INSERT INTO recensione(idutente,libro_isbn,datainserimento,valutazione,commento) VALUES 
(1000000004,9788893884167, '2020-02-20', 1, "Orribile, il mio falegname con 5 lire lo faceva meglio!!"),
(1000000004,9788879554596, '2020-02-26', 5, "Stupendo!!"),
(1000000004,9788830901988, '2020-02-26', 4, "Bello...Mi sono piaciute le descrizioni."),
(1000000004,9791280034816, '2020-04-15', 2, "Robaccia per adolescenti..."),
(1000000004,9788822760265, '2020-04-20', 1, "Uno dei libri più brutti che abbia mai letto nel corso della mia esistenza!"),
(1000000004,9788820362713, '2020-05-13', 1, "L'autore dovrebbe tornare a scuola..."),
(1000000004,9788861026223, '2020-04-15', 5, "OMG!! Emozionante!!"),
(1000000004,9788861895034, '2020-04-20', 2, "Noioso..."),
(1000000004,9783836589055, '2020-04-26', 3, "Non male!"),
(1000000004,9788808064851, '2020-01-16', 4, "Bellissimo libro ma il finale non mi ha convinto!"),
(1000000004,9791220500548, '2020-06-11', 2, "Personaggi noiosi e dialoghi poveri."),
(1000000004,9791280022486, '2020-05-16', 4, "Entusiasmante!"),
(1000000004,9791280229366, '2020-07-05', 5, "Un classico della letteratura!"),
(1000000004,9788868685768, '2020-07-05', 5, "Stupendo!! Finale epico!!"),
(1000000004,9788836210800, '2020-07-06', 4, "Molto bello ma con delle pecche"),
(1000000004,9788822628541, '2020-07-07', 2, "Mi aspettavo di meglio..."),
(1000000004,9788804728191, '2020-07-08', 3, "Libro discreto, nulla di più nulla di meno"),
(1000000004,9788822628725, '2020-07-09', 1, "Un libro orribile, con pessimi messaggi. Sconsigliato!"),
(1000000004,9788828764489, '2020-07-10', 4, "Ottimo libro, ben scritto, bei personaggi e dialoghi."),
(1000000004,9788828763604, '2020-07-11', 3, "Bene ma non benissimo, premessa interessante ma poteva essere esplorata meglio."),
(1000000004,9788804742364, '2020-07-12', 2, "Storia potenzialmente interessante rovinata da pessimi dialoghi."),
(1000000004,9791220376419, '2020-07-13', 5, "Spettacolo da inizio a fine, non posso che consigliarlo a tutti"),
(1000000004,9791259570321, '2020-07-14', 3, "Un libro che vuole essere tutto e finisce per non sapere di nulla. Non un brutto libro, ma nemmeno un ottimo libro."),
(1000000004,9788833751627, '2020-07-15', 2, "L'autore ha fatto il passo più lungo della gamba, libro rovinato dopo la metà. Peccato.."),
(1000000004,9788892841437, '2020-07-16', 4, "Un grande libro per un grande autore."),
(1000000004,9788809763180, '2020-07-17', 3, "Non uno dei suoi migliori, ma leggibile."),
(1000000004,9788830453517, '2020-07-18', 1, "Uno dei più grandi sprechi di soldi che abbia mai commesso..."),
(1000000004,9788832063479, '2020-07-19', 4, "Davvero bello, mi sono emozionato verso il finale."),
(1000000004,9788866884095, '2020-07-20', 3, "Carino, ma nulla di più."),
(1000000004,9788804742319, '2020-07-21', 5, "Semplicemente indescrivibile, rimarrà negli annali della letteratura."),
(1000000004,9788807900846, '2020-07-22', 3, "Degli spunti interessanti ma non sempre approfonditi come avrei voluto, discreto."),
(1000000004,9788807900693, '2020-07-23', 4, "Se non fosse per la parte iniziale un po' noiosa sarebbe stato perfetto."),
(1000000004,9788858333037, '2020-07-24', 2, "L'autore non è più lo stesso da anni ormai, sono deluso."),
(1000000004,9788868957636, '2020-07-25', 5, "Intrattenimento puro, mi ha lasciato estasiato."),
(1000000004,9788858018460, '2020-07-26', 4, "Libro molto bello con delle piccole pecche che non inficiano sulla sua godibilità."),
(1000000004,9788809898059, '2020-07-27', 3, "Senza infamia e senza lode, non male ma poteva essere di più."),
(1000000004,9798685585967, '2020-07-28', 2, "Dei personaggi divertenti lo salvano, ma non lo portano alla sufficienza."),
(1000000004,9781092842280, '2020-07-29', 4, "Molto interessante, libro consigliato a tutte le età."),
(1000000004,9788875788049, '2020-07-30', 3, "Sufficiente ma non oltre. Per molti versi l'autore ha fatto un compitino."),
(1000000004,9798529792254, '2020-08-05', 2, "Libro che sembra scritto svogliatamente, ho letto di peggio ma certamente anche molto di meglio."),
(1000000004,9788829927302, '2020-08-06', 1, "Pessimo anche per un dilettante."),
(1000000004,9788829929801, '2020-08-08', 4, "Molto molto bello, con qualche accorgimento poteva essere un capolavoro."),
(1000000004,9798482387986, '2020-08-09', 3, "Non bello come altri libri dell'autore, ma si fa leggere."),
(1000000004,9788817144988, '2020-08-10', 5, "Uno dei migliori libri sulla piazza. Consigliatissimo!"),
(1000000004,9798650853428, '2020-08-12', 3, "Non malaccio, ma...tutto qui?"),
(1000000004,9798786890663, '2020-08-13', 2, "Pessimo. Non metto 1 per incoraggiamento."),
(1000000004,9788863365474, '2020-08-14', 4, "Libro eccellente."),
(1000000004,9788851157630, '2020-08-15', 4, "Ottimo titolo, tra i migliori nel suo genere."),
(1000000004,9788891534613, '2020-08-16', 5, "La perfezione nel suo genere, nulla da eccepire."),
(1000000004,9788891520180, '2020-08-17', 3, "Bello, ma con qualche riserva..."),
(1000000004,9783836559591, '2020-08-20', 2, "Una delusione su tutta la linea."),
(1000000004,9788865204528, '2020-08-21', 1, "Terrificante...dilettantesco in ogni aspetto."),
(1000000004,9781675491300, '2020-08-22', 4, "Davvero bello."),
(1000000004,9798709878938, '2020-08-23', 5, "Capolavoro totale, sarà ricordato negli anni"),
(1000000004,9798455911064, '2020-08-24', 4, "Quasi perfetto, ma perde qualche punto sul finale."),
(1000000004,9788834609484, '2020-08-25', 4, "Un altro ottimo titolo per l'autore, da non perdere per i fan."),
(1000000004,9798513926276, '2020-08-26', 3, "Discreto, nulla di più, nulla di meno."),
(1000000004,9788845278334, '2020-08-27', 3, "Un compitino, senza infamia e senza lode."),
(1000000004,9798796912720, '2020-08-28', 4, "Ottimo libro che rispetta le aspettative del lettore."),
